// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;

import {ERC20, IERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";

/**
 * @title Mintlet - a minimalist token creator.
 * @notice Create simple fixed supply tokens with a single contract call to {create}.
 * @dev It can be easily called from Etherscan - no UI or setup required.
 * @author Paul Reinholdtsen (reinholdtsen.eth)
 */
contract Mintlet is ERC20 {
    using SafeERC20 for IERC20;

    /**
     * @notice Create a Mintlet and send all of the newly minted tokens to the caller.
     * @param name_ The name of token.
     * @param symbol_ The symbol of the token.
     * @param supply The fixed supply of the token.
     */
    function create(string memory name_, string memory symbol_, uint256 supply) external returns (address token) {
        if (this == proto) {
            // forge-lint: disable-next-line(asm-keccak256)
            bytes32 salt = keccak256(abi.encode(msg.sender, name_, symbol_, supply));
            token = Clones.cloneDeterministic(address(proto), salt);
            Mintlet(token).__initialize(msg.sender, name_, symbol_, supply);
        } else {
            token = proto.create(name_, symbol_, supply);
            IERC20(token).safeTransfer(msg.sender, supply);
        }
        emit MintletCreated(msg.sender, token, name_, symbol_, supply);
    }

    function __initialize(address issuer, string memory name_, string memory symbol_, uint256 supply) public {
        // Guard against anyone but proto initializing.
        if (msg.sender != address(proto)) {
            revert Unauthorized();
        }

        _name = name_;
        _symbol = symbol_;

        _mint(issuer, supply);
    }

    constructor() ERC20("", "") {
        proto = this;
    }

    Mintlet public immutable proto;
    string private _name;
    string private _symbol;

    function name() public view virtual override(ERC20) returns (string memory) {
        return _name;
    }

    function symbol() public view override(ERC20) returns (string memory) {
        return _symbol;
    }

    event MintletCreated(
        address indexed issuer, address indexed mintlet, string name, string indexed symbol, uint256 supply
    );

    error Unauthorized();
}

